// 2) Helper utilities
if (!function_exists('gymai_normalize_text')) {
	function gymai_normalize_text($text) {
		$text = trim(mb_strtolower($text, 'UTF-8'));
		$replacements = [
			'ي' => 'ی', 'ى' => 'ی', '‌' => ' ', 'ك' => 'ک',
			'ـ' => '', 'ٔ' => '', '‍' => '', '‏' => '',
		];
		$text = strtr($text, $replacements);
		$text = preg_replace('/\s+/', ' ', $text);
		return $text;
	}
}
if (!function_exists('gymai_make_unique_names')) {
	function gymai_make_unique_names(array $names) {
		$seen = [];
		$out  = [];
		foreach ($names as $n) {
			$n = trim($n);
			if ($n === '') continue;
			$norm = gymai_normalize_text($n);
			if (isset($seen[$norm])) continue;
			$seen[$norm] = true;
			$out[] = $n;
		}
		return array_values($out);
	}
}
if (!function_exists('gymai_exercise_exists_by_names')) {
	function gymai_exercise_exists_by_names(array $candidates) {
		$norms = [];
		foreach ($candidates as $c) {
			$c = trim($c);
			if ($c === '') continue;
			$norms[] = gymai_normalize_text($c);
		}
		if (empty($norms)) return false;

		foreach ($norms as $candidate) {
			$q = new WP_Query([
				'post_type'      => 'exercises',
				'post_status'    => 'any',
				'posts_per_page' => 1,
				'meta_query'     => [
					'relation' => 'OR',
					[
						'key'     => 'name_app',
						'value'   => $candidate,
						'compare' => 'LIKE',
					],
					[
						'key'     => 'other_names',
						'value'   => $candidate,
						'compare' => 'LIKE',
					],
				],
				'no_found_rows'  => true,
			]);
			if ($q->have_posts()) return true;
		}
		return false;
	}
}

// 3) Wipe existing exercises once (on first seed only)
function gymai_delete_all_exercises_once() {
	if (get_option('gymai_exercises_wiped_v2025_09_07')) return;

	$q = new WP_Query([
		'post_type'      => 'exercises',
		'post_status'    => 'any',
		'posts_per_page' => -1,
		'fields'         => 'ids',
		'no_found_rows'  => true,
	]);

	if ($q->have_posts()) {
		foreach ($q->posts as $pid) {
			wp_delete_post($pid, true);
		}
	}
	update_option('gymai_exercises_wiped_v2025_09_07', 1, false);
}

