// 5) Main seed (runs once; wipes first time; inserts base dataset)
function gymai_seed_exercises_v2025_09_07() {
	if (get_option('gymai_exercises_seeded_v2025_09_07')) return;
	if (function_exists('set_time_limit')) @set_time_limit(240);

	// Per request: wipe old, then seed fresh (only once)
	gymai_delete_all_exercises_once();

	$data = gymai_get_exercises_seed_data();

	foreach ($data as $ex) {
		$name   = isset($ex["name"]) ? trim($ex["name"]) : "";
		$others = isset($ex["other_names"]) && is_array($ex["other_names"]) ? $ex["other_names"] : [];
		if ($name === "") continue;

		$others     = gymai_make_unique_names($others);
		$candidates = gymai_make_unique_names(array_merge([$name], $others));

		if (gymai_exercise_exists_by_names($candidates)) continue;

		$post_id = wp_insert_post([
			"post_title"  => $name,
			"post_type"   => "exercises",
			"post_status" => "publish",
		]);

		if ($post_id && !is_wp_error($post_id)) {
			update_post_meta($post_id, "name_app", $name);
			update_post_meta($post_id, "other_names", $others);
			
			// ذخیره فیلدهای جدید
			if (isset($ex["main_muscle"])) {
				update_post_meta($post_id, "main_muscle", $ex["main_muscle"]);
			}
			if (isset($ex["secondary_muscles"])) {
				update_post_meta($post_id, "secondary_muscles", $ex["secondary_muscles"]);
			}
			if (isset($ex["difficulty"])) {
				update_post_meta($post_id, "difficulty", $ex["difficulty"]);
			}
			if (isset($ex["equipment"])) {
				update_post_meta($post_id, "equipment", $ex["equipment"]);
			}
			if (isset($ex["exercise_type"])) {
				update_post_meta($post_id, "exercise_type", $ex["exercise_type"]);
			}
			if (isset($ex["learn"])) {
				update_post_meta($post_id, "learn", $ex["learn"]);
			}
			if (isset($ex["tip_1"])) {
				update_post_meta($post_id, "tip_1", $ex["tip_1"]);
			}
			if (isset($ex["tip_2"])) {
				update_post_meta($post_id, "tip_2", $ex["tip_2"]);
			}
			if (isset($ex["tip_3"])) {
				update_post_meta($post_id, "tip_3", $ex["tip_3"]);
			}
			if (isset($ex["video_url"])) {
				update_post_meta($post_id, "video_url", $ex["video_url"]);
			}
			if (isset($ex["image_url"])) {
				update_post_meta($post_id, "image_url", $ex["image_url"]);
			}
			if (isset($ex["detailed_description"])) {
				update_post_meta($post_id, "detailed_description", $ex["detailed_description"]);
			}
			if (isset($ex["seo_content"])) {
				update_post_meta($post_id, "seo_content", $ex["seo_content"]);
			}
			
			// فیلدهای پیش‌فرض
			update_post_meta($post_id, "views_count", 0);
			update_post_meta($post_id, "likes_count", 0);
		}
	}

	update_option('gymai_exercises_seeded_v2025_09_07', 1, false);
}
